        put :update, :id => "1"
        assigns(:cmdline).should be(mock_cmdline)
      end

      it "re-renders the 'edit' template" do
        Cmdline.stub(:find) { mock_cmdline(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested cmdline" do
